        .send({
          name: 'testfolder',
          type: 'folder'
        })
        .expect(201);
      const folderId = folderRes.body.id;

      const res = await request(app)
        .get(`/files/${folderId}/data`)
        .set('X-Token', authToken)
        .expect(400);

      expect(res.body.error).to.equal("A folder doesn't have content");
